import Vuex from 'vuex'
import Vue from 'vue'

Vue.use(Vuex)
var store = new Vuex.Store({
    state: {
        showLogin: false
    },
    mutations: {
        changeShowLogin(state) {
            state.showLogin = !state.showLogin
        }
    },
})
export default store;
