import Vuex from 'vuex'
import Vue from 'vue'


var store = new Vuex.Store({
    state: {
        showLogin: true
    },
    mutations: {
        changeShowLogin(state) {
            state.showLogin = !state.showLogin
        }
    },
})
export default store;
